import UIKit

// MARK: - Connects View, Interactor, and Presenter
class EmailConpleteConfigurator {
    
    static let sharedInstance = EmailConpleteConfigurator()
    
    private init() {}
    
    // MARK: - Configuration
    
    func configure(_ viewController: EmailConpleteViewController) {
        let presenter = EmailConpletePresenter()
        presenter.viewController = viewController
        
        let interactor = EmailConpleteInteractor()
        interactor.presenter = presenter
        
        let router = EmailConpleteRouter()
        router.viewController = viewController
        router.dataStore = interactor
        
        viewController.interactor = interactor
        viewController.router = router
    }
}
